class DateUtil{static getCurrentDate(){let t=new Date;return t.setHours(0,0,0,0),t}}
class Logger{static log(){console.log(chrome.runtime.getManifest().name,...arguments)}static warn(){console.warn(chrome.runtime.getManifest().name,...arguments)}static error(){console.error(chrome.runtime.getManifest().name,...arguments)}static info(){console.info(chrome.runtime.getManifest().name,...arguments)}}
const URL=chrome.runtime.getManifest().config.databaseURL;class Rest{static async ajax(t,e=!1){if("string"!=typeof t)throw new Error("Passed paramter is not String");let a=`${URL}/${t}.json`;if(!e&&localStorage.getItem(t))return Storage.getItem(t);{const r=await fetch(a),s=await r.json();return e||Storage.setItem(t,s),s}}}
class Storage{static setItem(t,e){return"object"==typeof e?localStorage.setItem(t,JSON.stringify(e)):localStorage.setItem(t,e),!0}static getItem(t,e){let r=localStorage.getItem(t);if(null!=r)try{return JSON.parse(r)}catch(t){return r}return e||r}static removeItem(t){return localStorage.removeItem(t),!0}}